    bool chatting = false;
    string username = null;
    ChatClient client = null;
    int clientId;
    string message = null;
    string newMessage = null;
    List<Message> messages = new List<Message>();

    async Task Chat()
    {
        if (String.IsNullOrWhiteSpace(username))
        {
            message = "Please enter your name";
            return;
        }

        try
        {
            messages.Clear();
            client = new ChatClient(username, navigationManager);
            client.MessageReceived += MessageReceived;
            await client.StartAsync();
            chatting = true;
        }
        catch (Exception e)
        {
            message = $"{e.Message}";
        }
    }

    void MessageReceived(object sender, MessageReceivedEventArgs e)
    {
        bool isMine = false;
        if (!string.IsNullOrWhiteSpace(e.Username))
        {
            isMine = string.Equals(e.Username, username, StringComparison.CurrentCultureIgnoreCase);
        }
        var newMsg = new Message(e.Username, e.Message, isMine);
        messages.Add(newMsg);
        StateHasChanged();
    }

    async Task DisconnectAsync()
    {
        if (chatting)
        {
            await client.StopAsync();
            client = null;
            message = "chat is ended";
            chatting = false;
        }
    }
    async Task SendAsync()
    {
        if (chatting && !string.IsNullOrWhiteSpace(newMessage))
        {
            await client.SendAsync(newMessage);
            newMessage = "";
        }
    }
    class Message
    {
        public Message(string username, string body, bool mine)
        {
            Username = username;
            Body = body;
            Mine = mine;
        }
        public string Username { get; set; }
        public string Body { get; set; }
        public bool Mine { get; set; }
        public string username;

        public string CSS
        {
            get
            {
                return Mine ? "sent" : "received";
            }
        }
    }

